import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class LongestPalindromicSubstringTest {
    @Test
    void testcase1() {
        String s = "babad";
        String out = "aba";
        assertEquals(out, LongestPalindromicSubstring.longestPalindrome(s));
    }

    @Test
    void testcase2() {
        String s = "";
        String out = "";
        assertEquals(out, LongestPalindromicSubstring.longestPalindrome(s));
    }
}
